int maxConsecutive1s(int arr[], int n){
    int res = 0, curr = 0;

    for(int i = 0; i < n; i++){
        if(arr[i] == 1) curr++;

        else{
            res = max(curr, res);
            curr = 0;
        }
    }
    return max(res, curr);
    
}

